import moment from "moment";
const SessionLength = ({ setSessionTime, sessionTime }) => {

  const incrementSessionTime = () => {
    const maxTime = 60 * 60; 
    const newSessionTime = sessionTime >= maxTime ? maxTime : sessionTime + 60;
    setSessionTime(newSessionTime)
    }

  const decrementSessionTime = () => {
    const newSessionTime = sessionTime - 60;
    if (newSessionTime <= 0) {
      setSessionTime(0)
    }  else {
      setSessionTime(newSessionTime)
    }   
  }
  const formatSessionTime = moment.duration(sessionTime, "s").minutes();

    return (
      <div id="session-container">
        <h2 id="session-label">Session Length
          <p id="session-length">{formatSessionTime}</p>
        </h2>
        <button className="border border-black rounded m-2" id="session-increment" onClick={incrementSessionTime}>Session Up</button>
        <button className="border border-black rounded m-2" id="session-decrement" onClick={decrementSessionTime}>Session Down</button>
      </div>
    )
}
export default SessionLength;
