import PropTypes from "prop-types"
const ControlInterval = ({ time, setTime, type }) => {
  const maxTime = 60 * 60; 

  const incrementTime = () => {
    const newTime = time >= maxTime ? maxTime : time + 60;
    setTime(newTime)
    }
  console.log({time})
  const decrementTime = () => {
    const newTime = time <= 0 ? 0 : time - 60; 
    setTime(newTime) 
  }


    return (
      <div id={`${type}-container`}>
        <h2 id={`${type}-label`}>
          <p id={`${type}-length`}>{time/60}</p>
        </h2>
        <button className="border border-black rounded m-2" id={`${type}-increment`} onClick={incrementTime}>{type}</button>
        <button className="border border-black rounded m-2" id={`${type}-decrement`} onClick={decrementTime}>Down</button>
      </div>
    )
}
ControlInterval.propTypes = {
  time: PropTypes.number.isRequired, 
  setTime: PropTypes.func.isRequired,
  type: PropTypes.oneOf(["session", "break"]).isRequired
}
export default ControlInterval;
