import React, { useEffect, useState } from "react";
import alarm from "./alarm.wav";

const TimeLeft = ({ sessionTime, breakTime, timer, setTimer, onBreak, setOnBreak, intervalId, setIntervalId }) => {



    useEffect(() => {
        setTimer(onBreak ? sessionTime : breakTime)
        setOnBreak(!onBreak)
    }, [timer === 0]);

    const intervalOn = intervalId !== null;
    const handleStartStop = () => {
        if (intervalOn) {
            clearInterval(intervalId)
            setIntervalId(null);
        } else {
            const newIntervalId = setInterval(() => {
                setTimer(prevTimer => {
                    const newTimer = prevTimer >= 1 ? prevTimer - 1 : 0;

                    return newTimer;
                })
            }, 100); setIntervalId(newIntervalId)
        }
    }

    const seconds = Math.floor(timer % 60);

    return (
        < div id="timer-label" className="border-2 border-black rounded">
            <p className="font-mono">Time Remaining:</p>
            <div id="time-left" className="font-mono m-5">
                {Math.floor(timer / 60)}:{seconds < 10 ? "0" + seconds : seconds}
            </div>
            <audio src={alarm}></audio>
            <button onClick={handleStartStop} className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
        </div>
    )
}
export default TimeLeft;
