import React, { useEffect, useState } from "react"; 

const TimeLeft = ({ sessionTime, breakTime, timer, setTimer, onBreak, setOnBreak }) => {
    const [intervalId, setIntervalId] = useState(null);


    useEffect(() => { 
        setTimer(onBreak ? sessionTime : breakTime)
        setOnBreak(!onBreak)
  }, [timer === 0]); 

    const intervalOn = intervalId !== null;  
    const handleStartStop = () => {
        if (intervalOn) {
            clearInterval(intervalId)
            setIntervalId(null); 
        } else {
            const newIntId = setInterval(() => {
                setTimer(prevTimer => {
                    const newTimer = prevTimer - 1;
                    if (newTimer > 0) {
                        return prevTimer - 1;
                    } return prevTimer

                })

            }, 1000); setIntervalId(newIntervalId)
        }
    }


    return (
        < div id="timer-label" className="border-4 border-black bg-stone-200">
            <div id="time-left">{timer}</div>
        <button onClick={handleStartStop} className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
      </div>
    )
} 
export default TimeLeft;
