import React, { useState, useEffect } from "react";
import ControlInterval from "./ControlInterval";
import TimeLeft from "./TimeLeft";

//const alert = [{ name: "alert", audio: alarmAudio }];

const StudyClock = () => {

  const [sessionTime, setSessionTime] = useState(25 * 60);
  const [breakTime, setBreakTime] = useState(5 * 60);
  const [onBreak, setOnBreak] = useState(false);
  const [timer, setTimer] = useState(sessionTime);

  useEffect(() => {
    setTimer(onBreak)
  },[sessionTime, breakTime])

  const controlReset = () => {
    setSessionTime(25 * 60);
    setBreakTime(5 * 60);
    setTimer(sessionTime);
  }

  return (
    <div id="container" className="h-screen">

      <ControlInterval
        setTime={setBreakTime}
        time={breakTime}
        type="break" />

      <ControlInterval
        setTime={setSessionTime}
        time={sessionTime}
        type="session" />

      <TimeLeft
        onBreak={onBreak}
        setOnBreak={setOnBreak}
        timer={timer}
        setTimer={setTimer}
        sessionTime={sessionTime}
        breakTime={breakTime} />

      <button onClick={controlReset} className="border border-black rounded m-2" id="reset">reset</button>
    </div>
  );
}

export default StudyClock;
