import React, { useState } from "react";
import alarm from "./alarm.wav"; 



const StudyClock = () => {
  const [displayTime, setDisplayTime] = useState(25 * 60);
  const [sessionTime, setSessionTime] = useState(25 * 60); 
  //going to need multiple of these: one for time, session, break, increment, decrement, start+stop, reset

  //time needs to be in mm:ss

  const formatTime = (time) => {
    let minute = Math.floor(time / 60); 
  }

  const formatTimeLeft = (time) => {
    let minutes = Math.floor(time / 60);
    let seconds = Math.floor(minutes/60); 
    return (minutes < 10 ? "0" + minutes : minutes) + ":" + (seconds<10 ? "0"+ seconds: seconds)
  }


  return (
    <div id="container" className="flex justify-center">

      <div className="border border-black m-2">
        <p id="break-label">Break Length</p>
        <div id="break-length"></div>
        <button className="border border-black rounded m-2" id="break-increment">Break Up</button>
        <button className="border border-black rounded m-2" id="break-decrement">Break Down</button>
      </div>

      <SessionLength
        time={sessionTime}
        formatTime={formatTime}
      />

      <div id="timer-label" className="border border-black m-2">
        <div id="time-left">{formatTimeLeft(displayTime)}</div>
        <button className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
        <button className="border border-black rounded m-2" id="reset">reset</button>
      </div>
    </div>
  );



  function SessionLength({changeTime, formatTime, time }) {
    return (
      <div id="session-length">
        <h2 id="session-label">Session Length</h2>
        <button className="border border-black rounded m-2" id="session-increment">Session Up</button>
        <button className="border border-black rounded m-2" id="session-decrement">Session Down</button>
      </div>
    )
  }
}

export default StudyClock;
