import FormatDuration from "moment-duration-format"; 
import React, { useEffect } from "react"; 

FormatDuration(moment); 

const TimeLeft = ({ sessionTime, breakTime, timer, setTimer, onBreak, setOnBreak }) => {
    const formatTimeLeft = moment.duration(timer, "s").format("mm:ss");
    if (!onBreajk) {
        useEffect(() => { 
            setTimer(sessionTime)
        }, [sessionTime])
}



    return (
        < div id="timer-label" className="border-4 border-black bg-stone-200">
            <div id="time-left">{formatTimeLeft}</div>
        <button  className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
      </div>
    )
} 
export default TimeLeft;
