const SessionLength = ({ time, setTime}) => {
  const maxTime = 60 * 60; 

  const incrementTime = () => {
    const newTime = time >= maxTime ? maxTime : time + 60;
    setTime(newTime)
    }
  console.log({time})
  const decrementTime = () => {
    const newTime = time <= 0 ? 0 : time - 60
    if (newTime <= 0) {
      setTime(0)
    }  else {
      setTime(newTime)
    }   
  }
  const formatSessionTime = time / 60;  

    return (
      <div id="session-container">
        <h2 id="session-label">Session Length
          <p id="session-length">{formatSessionTime}</p>
        </h2>
        <button className="border border-black rounded m-2" id="session-increment" onClick={incrementTime}>Session Up</button>
        <button className="border border-black rounded m-2" id="session-decrement" onClick={decrementTime}>Session Down</button>
      </div>
    )
}
export default SessionLength;
