import React, { useState } from "react";
import BreakLength from "./BreakLength";
import SessionLength from "./SessionLength";
import moment from "moment-duration-format"; 

//const alert = [{ name: "alert", audio: alarmAudio }];

const StudyClock = () => {

  const [sessionTime, setSessionTime] = useState(25 * 60);
  const [breakTime, setBreakTime] = useState(5 * 60);
  const [onBreak, setOnBreak] = useState(false);
  const [timerOn, setTimerOn] = useState(false);
  const [displayTime, setDisplayTime] = useState(25 * 60);


  const controlTimer = () => {
      let second = 1000;
      let date = new Date().getTime();
      let nextDate = new Date().getTime() + second; 
      let isOnBreak = onBreak; 

      if (!timerOn) {
        let interval = setInterval(() => {
          date = new Date().getTime()
          if (date > nextDate) {
              setDisplayTime((prev) => {
                  return prev - 1;
              });
            } nextDate += second;
        }, 30)
      }
      setTimerOn(!timerOn)
  }

  const controlReset = () => {
    setSessionTime(25 * 60);
    setBreakTime(5 * 60);
  }
  return (
    <div id="container" className="h-screen">

      <BreakLength
        breakTime={breakTime}
        setBreakTime={setBreakTime}
      />

      <SessionLength
        setSessionTime={setSessionTime}
        sessionTime={sessionTime} />

      < div id="timer-label" className="border-4 border-black bg-stone-200">
        <div id="time-left"></div>
        <button onClick={controlTimer} className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
      </div>
      <button onClick={controlReset} className="border border-black rounded m-2" id="reset">reset</button>
    </div>
  );
}

export default StudyClock;
