import React, { useEffect } from "react";
import alarm from "./alarm.wav";

const TimeLeft = ({ sessionTime, breakTime, timer, setTimer, onBreak, setOnBreak, intervalId, setIntervalId }) => {

    const playAudio = () => {

    }

    useEffect(() => {
        setTimer(onBreak ? sessionTime : breakTime)
        setOnBreak(!onBreak)
    }, [timer === 0]);

    const intervalOn = intervalId !== null;
    const handleStartStop = () => {
        if (intervalOn) {
            clearInterval(intervalId)
            setIntervalId(null);
        } else {
            const newIntervalId = setInterval(() => {
                setTimer(prevTimer => {
                    const newTimer = prevTimer >= 1 ? prevTimer - 1 : 0;

                    return newTimer;
                })
            }, 100); setIntervalId(newIntervalId)
        }
    }

    const seconds = Math.floor(timer % 60);

    return (
        < div id="timer-label" className="border-2 border-black uppercase rounded">
            <p className="text-center m-2 font-mono">Time Remaining:</p>
            <div id="time-left" className="text-center font-mono m-5">
                {Math.floor(timer / 60)}:{seconds < 10 ? "0" + seconds : seconds}
            </div>
            <audio id="beep" src={alarm}></audio>
            <button onClick={handleStartStop} className="border-2 border-black p-1 rounded m-2" id="start_stop">Start/Stop</button>
        </div>
    )
}
export default TimeLeft;
