function BreakLength({ setBreakTime, breakTime, formatTime, time }) {

    const incrementTime = () => {
        setBreakTime(breakTime + 60)
    }

    const decrementTime = () => {
        setBreakTime(breakTime - 60)
    }

    return (
      <div id="break-length">
        <h2 id="break-label">{onLoadformatTime(breakTime)}</h2>
        <button className="border border-black rounded m-2" id="break-increment" onClick={incrementTime}>Break Up</button>
        <button className="border border-black rounded m-2" id="break-decrement" onClick={decrementTime}>Break Down</button>
      </div>
    )
}
export default BreakLength;
