import React, { useState } from "react";
import alarmAudio from "./alarm.wav"; 
import BreakLength from "./BreakLength";
import SessionLength from "./SessionLength";

const alert = [{ name: "alert", audio: alarmAudio }];

const StudyClock = () => {

  const [sessionTime, setSessionTime] = useState(25 * 60);
  const [breakTime, setBreakTime] = useState(5);
  const [onBreak, setOnBreak] = useState(false);  
  //going to need multiple of these: one for session, break, 

  //time needs to be in mm:ss

  const formatTime = (time) => {
    let minute = Math.floor(time / 60);
    return (minute)
  }

  const formatTimeLeft = (time) => {
    let minutes = Math.floor(time / 60);
    let seconds = Math.floor(minutes / 60);
    return (minutes < 10 ? "0" + minutes : minutes) + ":" + (seconds < 10 ? "0" + seconds : seconds)
  }


  return (
    <div id="container" className="flex justify-center">

      <BreakLength
        breakTime={breakTime}
        setBreakTime={setBreakTime}
         />

      <SessionLength
        setSessionTime={setSessionTime}
        sessionTime={formatTime(sessionTime)} />

      <div id="timer-label" className="border border-black m-2">
        <div id="time-left">{formatTimeLeft(onBreak ? breakTime : sessionTime)}</div>
        <button onClick={()=>setOnBreak(!onBreak)} className="border border-black rounded m-2" id="start_stop">Start/Stop</button>
        <button className="border border-black rounded m-2" id="reset">reset</button>
      </div>
    </div>
  );
}

export default StudyClock;
