import React, { useState } from "react";


const StudyClock = () => {
  const [displayTime, setDisplayTime] = React.useState(25 * 60);
  //going to need multiple of these: one for increment, decrement, start, and stopping 

  //time needs to be in mm:ss

  const formatTime = (time) => {
    let minutes = Math.floor(time / 60);
    let seconds = Math.floor(minutes/60); 
    return (minutes < 10 ? "0" + minutes: minutes) + ":" + (seconds < 10 ? "0"+seconds: seconds)
  }

  return (
    <div id="container" className="">
      <div id="break-label"><p>Break Length</p>
        <div id="break-length"></div>
        <button id="break-increment"></button>
        <button id="break-decrement"></button>
      </div>

      <div id="session-label"><p>Session Length</p>
        <div id="session-length">{formatTime(displayTime)}</div>
        <button id="session-increment">Session Increment</button>
        <button id="session-decrement"></button>
      </div>

      <div id="timer-label">
        <div id="time-left"></div>
        <button id="start_stop"></button>
        <button id="reset"></button>
      </div>
    </div>
  );
}

export default StudyClock;
