import React from "react"; 
import moment from "moment-duration-format"; 

const TimeLeft = ({ onBreak, timerOn, setDisplayTime, displayTime }) => {

const formatDisplayTime = moment.duration(displayTime).format("mm:ss")
    const controlTimer = () => {
        let second = 1000;
        let date = new Date().getTime();
        let nextDate = new Date().getTime() + second; 
        let isOnBreak = onBreak; 

        if (!timerOn) {
          let interval = setInterval(() => {
            date = new Date().getTime()
            if (date > nextDate) {
                setDisplayTime((prev) => {
                    return prev - 1;
                });
              } nextDate += second;
          }, 30)
        }
    }

return (
    <div id="timer-label" className="border-4 border-black bg-stone-200">
        <div id="time-left">{onBreak ? breakTime : sessionTime }</div>
        <button onClick={controlTimer} className="border border-black rounded m-2" id="start_stop">Start/Stop</button> 
        </div>
  ); 
}
export default TimeLeft;
