import React, { useState, useEffect } from "react";
import ControlInterval from "./ControlInterval";
import TimeLeft from "./TimeLeft";
import alarm from "./alarm.wav";

 const StudyClock = () => {

  const [sessionTime, setSessionTime] = useState(25 * 60);
  const [breakTime, setBreakTime] = useState(5 * 60);
  const [timer, setTimer] = useState(sessionTime);
  const [intervalId, setIntervalId] = useState(null);
  const [sessionType, setSessionType] = useState("Session"); 

  const alert = { name: "timer-alarm", audio: alarm }
  const seconds = Math.floor(timer % 60);
  const intervalOn = intervalId !== null;

   useEffect(() => {
     if (timer === 0) {
      //play audio 
       if (sessionType === "Session") {
         setSessionType("Break"); 
         setTimer(breakTime); 
       } else if (sessionType === "Break") {
         setSessionType("Session");
         setTimer(sessionTime)

       }
    }
  }, [breakTime, sessionTime, sessionType, timer])

  useEffect(() => {
    setTimer(sessionType ? sessionTime : breakTime) 
  }, [sessionTime, breakTime, sessionType])

  const handleStartStop = () => {
    if (intervalOn) {
        clearInterval(intervalId)
        setIntervalId(null);
    } else {
        const newIntervalId = setInterval(() => {
            setTimer(prevTimer => {
                const newTimer = prevTimer - 1;
                if (newTimer >= 0) {
                    console.log({newTimer})
                    return newTimer;
                } else if (newTimer === 0) {
                  return 0;
                }

            })   
        }, 100); setIntervalId(newIntervalId)
    }
}


  const controlReset = () => {
    clearInterval(intervalId)
    setIntervalId(null); 
    setSessionTime(25 * 60);
    setBreakTime(5 * 60);
    setTimer(sessionTime);
    setSessionType("Session"); 
  }

  return (
    <div id="container" className="flex h-screen justify-center items-center ">

      <ControlInterval
        setTime={setBreakTime}
        time={breakTime}
         type="break" />

        <TimeLeft
        timer={timer}
        sessionType={sessionType}
        handleStartStop={handleStartStop}
        seconds={seconds}
        intervalOn={intervalOn}
        TimeLeft={TimeLeft}
         />

      <ControlInterval
        setTime={setSessionTime}
        time={sessionTime}
        type="session" />

      <button onClick={controlReset} className="border-2 border-stone-400 rounded m-2 p-2 font-mono hover:border-red-700" id="reset">reset</button>
    </div>
  );
}

export default StudyClock;
