import React from "react"; 
import moment from "moment"; 

const BreakLength = ({ setBreakTime, breakTime }) => {

  const incrementBreakTime = () => {
    setBreakTime(breakTime + 60)

    }


  const decrementBreakTime = () => {
    const newBreakTime = breakTime - 60;
    if (newBreakTime <= 0) {
          setBreakTime(0)
    } else {
      setBreakTime(newBreakTime)
        }
  }
  const formatBreakTime = moment.duration(breakTime, "s").minutes();

    return (
      <div id="break-container" className="">
        <h2 id="break-label">Break Length
          <p id="break-length">{formatBreakTime}
          </p>
        </h2>
        <button className="border border-black rounded m-2" id="break-increment" onClick={incrementBreakTime}>Break Up</button>
        <button className="border border-black rounded m-2" id="break-decrement" onClick={decrementBreakTime}>Break Down</button>
      </div>
    )
}
export default BreakLength;
